void print_array(uint8_t* array, int len){
#if(VERBOSE)
	HAL_UART_Transmit(&hlpuart1, array, len, 0xFFFF);
#endif
}

void print_now(const char* string){
	print_array((uint8_t *)string, strlen(string));
}

void print_int(int my_int){

#if(VERBOSE)
	if (my_int == 0){
		print_array((uint8_t*)"0", 1);
		return;
	}
	char intbuffer[21]; //Consider we won't have more than 20 digits
	intbuffer[20] = '\0';
	uint8_t negative = 0;

	if(my_int<0){
		negative = 1;
		my_int = -my_int;
	}

	int k = 20;
	while(my_int>0){
		intbuffer[--k]=my_int%10+48;
		my_int /= 10;
	}
	if(negative){
		intbuffer[--k]='-';
	}
	print_now((char*)(intbuffer+k));

#endif
}
void print_error(const char* string, int my_int){
#if(VERBOSE)
	print_now(string);
	print_int(my_int);
	print_now("\r\n");
#endif
}

void print_time(void)
{
 RTC_DateTypeDef gDate;
 RTC_TimeTypeDef gTime;
/* Get the RTC current Time */
 HAL_RTC_GetTime(&hrtc, &gTime, RTC_FORMAT_BIN);
/* Get the RTC current Date */
 HAL_RTC_GetDate(&hrtc, &gDate, RTC_FORMAT_BIN);
/* Display time Format: hh:mm:ss */
 print_now("Hour: ");
 print_int(gTime.Hours);
 print_now(":");
 print_int(gTime.Minutes);
 print_now(":");
 print_int(gTime.Seconds);
 print_now(":");
 print_int(gTime.SubSeconds);
 print_now(" Date: ");
/* Display date Format: dd-mm-yy */
 print_int(gDate.Date);
 print_now("-");
 print_int(gDate.Month);
 print_now("-");
 print_int(2000 + gDate.Year);
 print_now("\r\n");
}
uint32_t get_time(void)
{
	 RTC_TimeTypeDef gTime;
/* Get the RTC current Time */
 HAL_RTC_GetTime(&hrtc, &gTime, RTC_FORMAT_BIN);


 uint32_t result =gTime.Hours;
 result <<= 8;
 result += gTime.Minutes;
 result <<= 8;
 result += gTime.Seconds;
 result <<= 8;
 result += gTime.SubSeconds;
 return result;

}
void blink(int time_ms){

	HAL_GPIO_TogglePin(GPIOA, GPIO_PIN_5);
	HAL_Delay(time_ms);
	HAL_GPIO_TogglePin(GPIOA, GPIO_PIN_5);
}

/* GPIO button press callback */
void HAL_GPIO_EXTI_Callback(uint16_t GPIO_Pin)
{
	//while(1) 	  print_now("");

	//debug_print("GPIO EXTI callback - ");
	if(GPIO_Pin == Button1_Pin) {
		print_now("Button 1\r\n");
		batext_power_on();
		blink(500);
	} else if (GPIO_Pin == Button2_Pin) {
		print_now("Button 2\r\n");
		print_now("Shutting down\r\n");
		batext_power_off();
		blink(500);
		print_now("List of time index with detection: \r\n");

		for(int i = 0 ; i < time_index; i++){
			print_int(registered_times[i]);
			print_now("\r\n");
		}
	} else {
		print_now("other?\r\n");
	}
}


void HAL_RTCEx_WakeUpTimerEventCallback(RTC_HandleTypeDef *hrtc)
{

	print_now("RTC Wake up !\r\n");
}

volatile uint8_t counting_cycles = 0;

void start_cycle_count() {
	uint32_t prim = __get_PRIMASK();
	__disable_irq();
	if (counting_cycles) {
		print_now("Tried re-entrant cycle counting.\r\n");
		Error_Handler();
	} else {
		counting_cycles = 1;
	}
	if (!prim) {
		__enable_irq();
	}
	DWT->CTRL |= 1 ; // enable the counter
	DWT->CYCCNT = 0; // reset the counter
}
void stop_cycle_count(char *s) {
	uint32_t res = DWT->CYCCNT;
	counting_cycles = 0;
	print_now("[PERF] ");
	print_now(s);
	print_error("Cycles: ", res);
}


